import "core-js/modules/es.array.push.js";
import crimeJson from '../../../backend/data/CrimeDate.json';
import eventJson from '../../../backend/data/temp.json';
export default {
  data() {
    return {
      center: {
        lat: 38.5509409,
        lng: -121.7358303
      },
      crimeMarkers: [],
      eventMarkers: []
    };
  },
  mounted() {
    for (var i = 0; i < crimeJson.length; i++) {
      var data = {
        position: {
          lat: crimeJson[i].lat,
          lng: crimeJson[i].lng
        }
      };
      this.crimeMarkers.push(data);
    }
    for (i = 0; i < eventJson.length; i++) {
      data = {
        position: {
          lat: eventJson[i].lat,
          lng: eventJson[i].lng
        }
      };
      this.eventMarkers.push(data);
    }
  }
};
